INSERT INTO `questions` (`question_text`, `option_a`, `option_b`, `option_c`, `option_d`, `correct_answer`, `category`, `difficulty`, `points`, `status`) VALUES
('ما هو الغرض الأساسي من استخدام "حزام البريم" المصنوع من الجلد الذي يلفه الرجل والمرأة على الخصر في البادية؟', 'لتثبيت البشت.', 'للدلالة على المكانة الاجتماعية.', 'لتزيين المظهر.', 'لرفع الثوب أثناء تأدية الأعمال وشد الظهر.', 'D', 'أزياء', 'سهل', 10, 'active'),
('في حالة عدم توفر القماش، كان يتم غسل وتفصيل "ثوب ردن" للأطفال من أي خامة؟', 'القطن', 'الحرير', 'الصوف', 'الجلد', 'A', 'أزياء', 'سهل', 10, 'active'),
('ما هو الزي الذي كان مفتوحاً من الأمام، ويلبس في فصل الشتاء والمناسبات، وعُرف لدى قبيلتي عنزة وشمر ولدى "الخويا" (مرافقة الشخصيات الحكومية)؟', 'الجبة', 'الزبون أو الصاية', 'المرودن', 'البدي', 'B', 'أزياء', 'متوسط', 15, 'active'),
('ما هي القطعة التي تلبس تحت الشماغ أو الغترة، وتعرف باسم "قبع" لدى بعض القبائل؟', 'العقال', 'الغترة', 'الطاقية', 'البشت', 'C', 'أزياء', 'سهل', 10, 'active'),
('ما هو نوع البشت الذي يرتديه شيوخ القبائل، والذي يُصنع من الصوف الناعم ويزخرف بخيوط قيطان ذهبية؟', 'المزوية', 'الشمالي', 'الدربوية', 'السويعي', 'D', 'أزياء', 'متوسط', 15, 'active'),
('أين تكون الزخرفة على هيئة أهداب متدلية في العقال؟', 'حول فتحة الرقبة', 'على الأطراف الأمامية', 'من الخلف تصل إلى أسفل الظهر', 'في الوسط حول الرأس', 'C', 'أزياء', 'سهل', 10, 'active'),
('ما هي قطعة القماش المستطيلة التي يبلغ طولها حوالي مترين، وتستخدم كعمامة فوق الغترة أو الشماغ لرجال الدين والشيوخ؟', 'الشالكو', 'مروجنة', 'المعّم', 'البريم', 'C', 'أزياء', 'متوسط', 15, 'active');
