INSERT INTO `questions` (`question_text`, `option_a`, `option_b`, `option_c`, `option_d`, `correct_answer`, `category`, `difficulty`, `points`, `status`) VALUES
('ما هو المعنى الدقيق لعبارة "ابعد مِشْحاه"؟', 'أي أنه سار مسافة طويلة ووصل إليه بطريقة سريعة.', 'أي أنه ابتعد وهاجر إلى مكان لم يعد يعرفه.', 'أي أبعده وأقصاه إلى مكان بعيد.', 'أي أنه سليم النية وطيب السمعة.', 'C', 'عبارات', 'سهل', 10, 'active'),
('عبارة "أبو العمرين وأبو الزمريْن" تُطلق على:', 'الشخص الذي يمتلك مكانة عالية في القوم.', 'صاحب الأفعال الطيبة وعظيم الشأن وطيب السمعة.', 'الرجل الذي يمتلك عمراً طويلاً وزوجتين.', 'المرأة التي تقول عن زوجها أنه "أبو العمرين والزمريْن" لتقصيره.', 'B', 'عبارات', 'سهل', 10, 'active'),
('ماذا يُقصد بمن "أخذ سُمُّ الحُمُّوس"؟', 'أي أنه تمهل وتأخر في إظهار الغضب.', 'أي أنه شُفي من غضبه وحممه.', 'أي أنه تمادى في الغضب.', 'أي كان أول من صب عليه الغاضب غضبه، أو أول من ضُرب في مشاجرة.', 'D', 'عبارات', 'متوسط', 15, 'active'),
('ما معنى عبارة "أخذ على باله"؟', 'أي أنه لم يحسب حساباً للأمر.', 'أي أنه نسي الموضوع تماماً.', 'أي عتب في نفسه على (عاتب نفسه).', 'أي أنه ركز انتباهه على الأمر.', 'C', 'عبارات', 'سهل', 10, 'active'),
('ما المقصود بعبارة "أخذ عليه النوم"؟', 'أي أنه نام نوماً خفيفاً.', 'أي أنه قاوم النعاس.', 'أي تغلّب عليه سلطان النوم.', 'أي أنه استيقظ من النوم.', 'C', 'عبارات', 'سهل', 10, 'active'),
('إذا قيل عن شخص "أخذ في حاله مقلّب" فمعنى ذلك أنه:', 'أي أنه تبدلت أحواله إلى الأفضل.', 'أي أنه كثير الانتقال والترحال.', 'أي أنه أخذ يتبجح ويتصرف بفخامة وكبرياء، ويظن نفسه أفضل من الآخرين.', 'أي أنه تغيرت طريقة تعامله مع الناس.', 'C', 'عبارات', 'متوسط', 15, 'active'),
('ماذا يُقصد بالعبارة "إرْكَدْ رَبَايِنْك"؟', 'أي تمهل وانتظر قليلاً.', 'أي كن مستعداً للمغادرة.', 'أي لا تتدخل فيما لا يعنيك أو في شأن ليس من شؤونك.', 'أي لا تبالغ في الكلام والمزاح.', 'C', 'عبارات', 'سهل', 10, 'active'),
('عبارة "أكْل مصخة" تعني أنه:', 'أكل بجهد كبير.', 'أكل ما هو مسخ أو فاسد.', 'أكل بشراهة.', 'أكل بسرعة.', 'B', 'عبارات', 'سهل', 10, 'active'),
('إذا قيل عن شخص "بِيْجِيْب الصَّوَاب"، فمعناها:', 'أنه يأتي بالمعلومة الخاطئة.', 'أي أن هذا الأمر يجعل الإنسان يفقد رشده وصوابه.', 'أي أنه يخالف رأي الجماعة.', 'أي أنه يتفق مع جميع الآراء.', 'B', 'عبارات', 'متوسط', 15, 'active'),
('إذا قيل عن شخص "بَرْدَاهْ"، فمعناه:', 'أنه شخص بارد المشاعر.', 'أنه شخص سريع الغضب.', 'أي أنه بسيط وطيب الأخلاق وحسن السيرة وذو فال حسن.', 'أنه يسعى لنيل الرزق والمال الوفير.', 'C', 'عبارات', 'سهل', 10, 'active');
